require 'largest_product_in_a_series'

describe LargestProduct do
  let(:test) { described_class.new }

  describe "#find_largest_product_in_series" do
    it "finds the largest product of sequences of length 2 in an int" do
      expect(test.find_largest_product_in_series(12345, 2)).to eq(20)
    end

    it "finds the largest product of sequences of length 4 in a large int" do
      large_int = 

      expect(test.find_largest_product_in_series(large_int, 4)).to eq(5832)
    end

    it "finds the largest product of sequences of length 13 in a large in" do
      large_int = 

      expect(test.find_largest_product_in_series(large_int, 13)).to eq(52)
    end
  end

  describe "#slice_array_into_sequences" do
    it "breaks an array into sequences of length 2" do
      test_array = [1, 2, 3, 4, 5]
      output_array = [[1, 2],[2, 3],[3, 4],[4, 5]]
      
      expect(test.slice_array_into_sequences(test_array, 2)).to eq(output_array)
    end

    it "breaks an array into sequences of length 3" do
      test_array = [1, 2, 3, 4, 5]
      output_array = [[1, 2, 3],[2, 3, 4],[3, 4, 5]]

      expect(test.slice_array_into_sequences(test_array, 3)).to eq(output_array)
    end
  end

  describe "#convert_int_to_array_of_digits" do
    it "takes int 123 and returns [1, 2, 3]" do
      expect(test.convert_int_to_array_of_digits(123)).to eq([1, 2, 3])
    end
  end
end
